import React, { useCallback, useState } from 'react';
import { useNavigate } from "react-router-dom";
import Navigation from "../inc/navigation";
import { useDropzone } from 'react-dropzone';
import Lottie from 'react-lottie';
import animationData1 from '../../assets/upload.json';
import '/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/pagesCSS/UploadCSS.css'; // Adjust the path according to your project structure
import cloudIconUrl from '/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/images/cloud.svg';

const defaultOptions1 = {
  loop: true,
  autoplay: true, 
  animationData: animationData1,
  rendererSettings: {
    preserveAspectRatio: 'xMidYMid slice'
  }
};

const Upload = () => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [result, setResult] = useState('');
  const items =[{lottieOptions: defaultOptions1}]

  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];
    if (file && file.type.startsWith('image/')) {
      setFiles([{
        ...file,
        preview: URL.createObjectURL(file)
      }]);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Simulate sending data to backend and receiving a result
    setShowModal(true);
    setResult("Image analysis result here"); // Placeholder for backend response
  };

  const handleCloseModal = () => {
    setShowModal(false);
    navigate('/dashboard'); // Adjust the navigate path as needed
  };

  const {getRootProps, getInputProps, isDragActive} = useDropzone({
    onDrop,
    multiple: false,
  });

  return (

    <div className="upload-container">
      {/* Your component structure */}
      <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
      <Lottie options={defaultOptions1} height={300} width={300} />
        <input {...getInputProps()} />
        {isDragActive ? <p>Drop the images here ...</p> : <p>Drag 'n' drop some images here, or click to select images</p>}
      </div>
      {/* Show the submit button only when an image is selected */}
      {files.length > 0 && <button onClick={handleSubmit} className="submit-button">Analyze Image</button>}
      {/* Modal for showing the result */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>&times;</span>
            <p>{result}</p>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}  
    </div>
  );
};

export default Upload;
