import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import '/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/pagesCSS/UploadCSS.css'
import cloudIconUrl from '/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/images/cloud.svg';
import { sendDataToBackend } from "../inc/apiService";
const Upload = () => {
  const [files, setFiles] = useState([]);

  const onDrop = useCallback(acceptedFiles => {
    // Since multiple is false, only take the first file even if user drops multiple
    const file = acceptedFiles[0];
    if (file && file.type.startsWith('image/')) {
      setFiles([{...file, preview: URL.createObjectURL(file)}]);
    }
  }, [setFiles]); // Depend on setFiles instead of files to avoid re-creating this callback unnecessarily

  const {getRootProps, getInputProps, isDragActive} = useDropzone({
    onDrop,
    multiple: false, // Only allow one file to be dropped at a time
  });

  return (

    <div className="upload-container">
      <Navigation />
      <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
        <img src={cloudIconUrl} alt="Cloud Upload Icon" className="cloud-icon" />
        <input {...getInputProps()} />
        {isDragActive ? (
          <p className="fontp">Drop the images here ...</p>
        ) : (
          <p>Drag 'n' drop some images here, or click to select images</p>
        )}
        {files.length > 0 && (
          <button type="button" className="submit-button">
            Submit
          </button>
        )}
      </div>

      <div className="preview">
        {files.map(file => (
          <div key={file.name} className="preview-image">
            <img src={file.preview} style={{width: '100%'}} alt="Preview" />
            <p>{file.name}</p>
          </div>
        ))}
      </div>
    </div>
  );

};

export default Upload;
