import React, { useCallback, useState } from 'react';
import { useNavigate } from "react-router-dom";
import Navigation from "../inc/navigation";
import Footer from "../inc/footer";
import { useDropzone } from 'react-dropzone';
import '/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/pagesCSS/UploadCSS.css'; // Adjust the path according to your project structure
import cloudIconUrl from './/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/images/cloud.svg';

const Upload = () => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [result, setResult] = useState(null);

  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];
    if (file && file.type.startsWith('image/')) {
      setFiles([{...file, preview: URL.createObjectURL(file)}]);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Your submit logic here, e.g., sending `files` to backend
    setShowModal(true); // Assume you want to show a modal on submit
  };

  const handleClose = () => {
    setShowModal(false);
    navigate("/dashboard");
  };

  const {getRootProps, getInputProps, isDragActive} = useDropzone({
    onDrop,
    multiple: false,
  });

  return (
    <div className="upload-container">
      <form onSubmit={handleSubmit} {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
        <input {...getInputProps()} />
        {isDragActive ? (
          <p>Drop the images here ...</p>
        ) : (
          <p>Drag 'n' drop some images here, or click to select images</p>
        )}
        {files.length > 0 && (
          <>
            <div className="preview">
              {files.map(file => (
                <div key={file.name} className="preview-image">
                  <img src={file.preview} alt="Preview" />
                  <p>{file.name}</p>
                </div>
              ))}
            </div>
            <button type="submit" className="submit-button">
              Analyze Image
            </button>
          </>
        )}
      </form>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleClose}>&times;</span>
            <p>{result}</p>
            <button onClick={handleClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Upload;
