import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import './UploadCSS.css'; // Ensure to style this according to the Block Convey style guide

const Upload = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];
    if (file && file.type.startsWith('image/')) {
      setSelectedImage({
        file: file,
        preview: URL.createObjectURL(file),
      });
    }
  }, []);

  const {getRootProps, getInputProps, isDragActive} = useDropzone({
    onDrop,
    multiple: false,
    accept: 'image/*',
  });

  const handleReupload = () => {
    setSelectedImage(null); // Clear the current selection
  };

  return (
    <div className="upload-container">
      <h1 className="upload-heading">Upload Your Brain Scan</h1>
      <div {...getRootProps()} className={`upload-box ${isDragActive ? 'active' : ''}`}>
        <input {...getInputProps()} />
        {selectedImage ? (
          <>
            <img src={selectedImage.preview} alt="Preview" className="image-preview" />
            <button type="button" onClick={handleReupload}>Reupload Image</button>
            <button type="button" className="submit-button">Analyze Image</button>
          </>
        ) : (
          <p>{isDragActive ? "Drop the images here ..." : "Drag 'n' drop some images here, or click to select images"}</p>
        )}
      </div>
    </div>
  );
};

export default App;
