import React, { useCallback, useState } from 'react';
import { useNavigate } from "react-router-dom";
import Navigation from "../inc/navigation";
import { useDropzone } from 'react-dropzone';
import Lottie from 'react-lottie';
import Footer from "../inc/footer";
import animationData1 from '../../assets/upload.json';
import '/Users/aditiakhauri/Desktop/Upload/new/Frontend/src/components/pagesCSS/UploadCSS.css'; // Adjust the path according to your project structure

const defaultOptions1 = {
  loop: true,
  autoplay: true, 
  animationData: animationData1,
  rendererSettings: {
    preserveAspectRatio: 'xMidYMid slice'
  }
};

const Upload = () => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [result, setResult] = useState('');
  const items =[{lottieOptions: defaultOptions1}]
  const [analyzing, setAnalyzing] = useState(false);

  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];
    if (file && file.type.startsWith('image/')) {
      setFiles([{
        ...file,
        preview: URL.createObjectURL(file)
      }]);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setAnalyzing(true);
    // Simulate sending data to backend and receiving a result
    setShowModal(true);
    setResult("Image analysis result here"); // Placeholder for backend response
  };
  const handleCancel = () => {
    setAnalyzing(false); // Show the Analyze Image button again
  };

  const handleAddToBlockchain = () => {
    // Logic for adding to blockchain
    console.log("Adding to blockchain...");
  };

  const handleCloseModal = () => {
    setShowModal(false);
    navigate('/dashboard'); // Adjust the navigate path as needed
  };

  const {getRootProps, getInputProps, isDragActive} = useDropzone({
    onDrop,
    multiple: false,
  });

  return (
    <div className="upload-container">
      <Navigation />
      <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
        {/* Conditionally render the Lottie animation and the "drag n drop..." text */}
        {files.length === 0 && ( // Only show if there are no files
          <>
            <Lottie options={defaultOptions1} height={300} width={300} />
            <input {...getInputProps()} />
            {isDragActive ? (
              <p>Drop the images here ...</p>
            ) : (
              <p>Drag 'n' drop some images here, or click to select images</p>
            )}
          </>
        )}

        {/* Preview uploaded image */}
        {files.map(file => (
          <div key={file.name} className="preview-image">
            <img src={file.preview} alt="Preview" />
            <p>{file.name}</p>
          </div>
        ))}
        {/* Show the Analyze Image button if an image is uploaded */}
        {files.length > 0 && (
          <div>
            <button onClick={handleSubmit} className="submit-button">
              Analyze Image
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Upload;
